mapper.select:
    SELECT * FROM project.companyboard 

    
        
            AND promotionType LIKE CONCAT('%',#{type},'%')
            
        
        
            AND duration IN
            
                #{promotionType}
            
        
        
            AND category IN
            
                #{promotionType}
            
        
        
            AND title LIKE CONCAT('%', #{search}, '%')
        
    
    ORDER BY companyBoardNum DESC;
